parameters.inputSchema:
={
  "type": "object",
  "required": ["title", "topics"],
  "properties": {
    "title": {
      "type": "string",
      "description": "A short, descriptive title"
    },
    "topics": {
      "type": "array",
      "description": "An array of exactly three topics",
      "minItems": 3,
      "maxItems": 3,
      "items": {
        "type": "string"
      }
    }
  },
  "additionalProperties": false
}
